import React, { useEffect } from 'react';
import { useObserver } from "mobx-react-lite"

//mobx数据
import useStore from "../../../utils/useStore"
import style from "./index.module.css"

const Cart: React.FC = () => {
  let store = useStore();
  let { cart } = store;
  useEffect(() => {
    //初次数据复制
    cart.setaAddress();
  }, [])

  let isShow = () => {

  }

  return useObserver(() => <>

    <div className={style.cart}>
      <div className={style.cartHead}>
        <p><span>★</span> 30天无忧退货</p>
        <p><span>★</span>48小时快速退款</p>
        <p><span>★</span>满88元免邮费</p>
      </div>
      <div className={style.cartMain}>
        {
          cart.cartList.map((item, index) => {
            return <div key={index} className={style.ListItem}>
              <div className={style.isCheckItem} onClick={() => {
                if (cart.num === 1) {
                  cart.oneSelect(item.checked, item.product_id)
                } else {
                  cart.setIndex(index)
                }

              }}>
                {cart.num === 1 ? <img src={item.checked === 1 ? "data:image/png;base64,"
                  : "data:image/png;base64,"} alt="" />
                  : <img src={cart.index.some((item) => item === index) ? "data:image/png;base64,"
                    : "data:image/png;base64,"} alt="" />
                }
              </div>
              <div className={style.Item}>
                <img src={item.list_pic_url} alt="" />
              </div>
              {cart.num === 1 ?
                <div className={style.commodity}>
                  <div className={style.ItemMsg}>
                    <div>{item.goods_name}</div>
                    <div></div>
                    <div>￥{item.retail_price}</div>
                  </div>
                  <div className={style.ItemNum}>x{item.number}</div>
                </div>
                : <div className={style.selected}>
                  <div className={style.selects}>已选择:</div>
                  <div className={style.countOp}>
                    <div className={style.color}>￥199</div>
                    <div className={style.num}>
                      <div onClick={()=>{
                        cart.setCount(item,"-")
                      }}>-</div>
                      <div>{item.number}</div>
                      <div onClick={() => {
                        cart.setCount(item, "+")
                      }}>+</div>
                    </div>
                  </div>
                </div>}
            </div>
          })
        }
      </div>
      {/* 这是底部 */}
      <div className={style.cartFooter}>
        {cart.num === 1 ? <div className={style.trade}>
          <div className={style.img} onClick={() => {
            cart.isSelect()
          }}>
            <img src={cart.cartTotal.checkedGoodsCount === cart.cartTotal.goodsCount ? "data:image/png;base64,"
              : "data:image/png;base64,"} alt="" />
          </div>
          <div className={style.select}>
            <p>
              <span>已选({cart.cartTotal.checkedGoodsCount})</span>
              <span>￥{cart.cartTotal.checkedGoodsAmount}</span>
            </p>
          </div>
          <div className={style.editor} onClick={() => {
            cart.numIS(0)
          }}>编辑</div>
          <div className={style.place}>下单</div>
        </div>
          //    这是删除页面
          : <div className={style.delete}>
            <div className={style.img} onClick={() => {
              cart.wholeDelete()
            }}>
              <img src={cart.whole ? "data:image/png;base64,"
                : "data:image/png;base64,"} alt="" />
            </div>
            <div className={style.select}>
              <p>
                <span>已选({cart.index.length})</span>
              </p>
            </div>
            <div className={style.editor} onClick={() => {
              cart.numIS(1)
            }}>完成</div>
            <div className={style.place} onClick={()=>{
            alert(111111)
            }}>删除所选</div>
          </div>
        }
      </div>
    </div>
  </>)
}

export default Cart;